options.script:
// Extracting the SQL data
let series = data.series[0];  // Access the data series
if (!series || series.length === 0 || series.fields[0].values.length === 0) {
  // Query result has no records, so skip the chart rendering
  return {
      data: [],
    layout: {
      title: 'No Data Available',
      height: 10, // Reduce panel height
      margin: {
        l: 20,  // Left margin
        r: 20,  // Right margin
        t: 40,  // Top margin
        b: 40   // Bottom margin
      }
    }
  };
}

// Start data processing
let height = series.fields[0];  // Block height
let voted = series.fields[1];   // Voted status (0 = Inactive, 1 = Missed, 2 = Voted, 3 = Proposed)
let moniker = series.fields[2]; // Moniker (Validator name)
let epoch = series.fields[3]; // Moniker (Validator name)

// Get unique monikers and heights from the dataset for rows and columns
let uniqueMonikers = [...new Set(moniker.values)].sort().reverse();  // Unique monikers for Y-axis
let uniqueHeights = [...new Set(height.values)].sort().reverse();    // Unique block heights for X-axis

// Initialize the 2D array (zValues) for the heatmap, default to 0 (Inactive)
let zValues = Array(uniqueMonikers.length).fill(null).map(() => Array(uniqueHeights.length).fill(0));
let hoverText = Array(uniqueMonikers.length).fill(null).map(() => Array(uniqueHeights.length).fill(""));

// Populate zValues with voted data (0 = Inactive, 1 = Missed, 2 = Voted, 3 = Proposed)
for (let i = 0; i < height.values.length; i++) {
  let rowIndex = uniqueMonikers.indexOf(moniker.values[i]);  // Find the correct row for the moniker
  let colIndex = uniqueHeights.indexOf(height.values[i]);    // Find the correct column for the height
  zValues[rowIndex][colIndex] = voted.values[i];  // Assign the voted status
  let statusMeaning = getStatusMeaning(voted.values[i]);  // Get the status meaning
  let epoch_number = epoch.values[i];
  hoverText[rowIndex][colIndex] = `x: ${height.values[i]}<br>y: ${moniker.values[i]}<br>z: ${statusMeaning}(epoch: ${epoch_number})`;  // Custom hover info with status meaning    
}

// Define a continuous color scale for smoother transitions
let colorScale = [
  [0, 'rgba(255, 0, 0, 0.5)'],    // Inactive = gray
  [0.33, 'rgba(0, 0, 0, 0.5)'],     // Missed = red
  [0.66, 'rgba(0, 0, 0, 0.5)'],       // Voted = black
  [1, 'rgba(0, 0, 0, 0.5)'],        // Nill = yellow
];


// Return the heatmap configuration for Plotly
return {
  data: [{
    z: zValues,               // 2D array of statuses (0, 1, 2, 3)
    x: uniqueHeights,         // Block heights for X-axis
    y: uniqueMonikers,        // Monikers for Y-axis
    type: 'heatmap',          // Plotly heatmap type
    colorscale: colorScale,   // Use only two colors for discrete values
    showscale: false,         // Hide the color scale bar
    zmin: 0,                  // Set the minimum value for z (status)
    zmax: 3,                  // Set the maximum value for z (status)
    xgap: 5,                  // Add gap between cells along the X-axis (adjust the value for bigger gaps)
    ygap: 5,                   // Add gap between cells along the Y-axis (adjust the value for bigger gaps)    
    text: hoverText,          // Set the hoverText for the heatmap
    hoverinfo: 'text'         // Display the hoverText    
  }],
  layout: {
    xaxis: {
      type: 'category',
      tickmode: 'linear',
      showgrid: true,          // Ensure gridlines on X-axis
      tickformat: '.0f',
      tickangle: -45,          // Rotate labels for better readability
    },
    yaxis: {
      tickmode: 'array',        // Use tickmode 'array' to manually set Y-axis labels
      tickvals: uniqueMonikers, // Set tick values to all unique monikers
      ticktext: uniqueMonikers, // Display all monikers as text on Y-axis
      showgrid: true            // Ensure gridlines on Y-axis
    },
    margin: {
      l: 200,  // Adjust margin for longer moniker names
      t: 20,   // Reduce top margin
      b: 120,  // Adjust bottom margin for X-axis labels
    }
  }
};

function getStatusMeaning(status) {
  switch(status) {
    case 0:
      return 'Absent';
    case 1:
      return 'Voted';
    default:
      return 'Unknown';
  }
}

