    },
    yaxis: {
      tickmode: 'array',        // Use tickmode 'array' to manually set Y-axis labels
      tickvals: uniqueMonikers, // Set tick values to all unique monikers
      ticktext: uniqueMonikers, // Display all monikers as text on Y-axis
      showgrid: true            // Ensure gridlines on Y-axis
    },
    margin: {
      l: 200,  // Adjust margin for longer moniker names
      t: 20,   // Reduce top margin
      b: 120,  // Adjust bottom margin for X-axis labels
    }
  }
};

// Function to map the voted status to its corresponding meaning
function getStatusMeaning(status) {
  switch(status) {
    case 0:
      return 'Inactive';
    case 1:
      return 'Missed';
    case 2:
      return 'Voted';
    case 3:
      return 'Proposed';
    default:
      return 'Unknown';
  }
}

// Function to shorten long moniker values
function shortenMoniker(moniker, startLength = 6, endLength = 4) {
  if (moniker.length > (startLength + endLength)) {
    return `${moniker.substring(0, startLength)}...${moniker.substring(moniker.length - endLength)}`;
  }
  return moniker;  // If it's already short, return as-is
}
