options.script:
let series = data.series[0];

if (!series || series.fields.length === 0 || series.fields[0].values.length === 0) {
  return {
    data: [],
    layout: {
      title: 'No Data Available',
      height: 10,
      margin: { l: 20, r: 20, t: 40, b: 40 }
    }
  };
}

let btcStakingTxHash = series.fields.find(f => f.name === 'btc_staking_tx_hash');
let covenantBtcPk = series.fields.find(f => f.name === 'covenant_btc_pk');
let status = series.fields.find(f => f.name === 'status');
let signedHeight = series.fields.find(f => f.name === 'signature_height');
let delegationHeight = series.fields.find(f => f.name === 'delegation_height'); // ‚úÖ delegation_height Ï∂îÍ∞Ä

if (!btcStakingTxHash || !covenantBtcPk || !status || !signedHeight || !delegationHeight) {
  return {
    data: [],
    layout: {
      title: 'Invalid Data',
      height: 10,
      margin: { l: 20, r: 20, t: 40, b: 40 }
    }
  };
}

function trimLabel(label) {
  if (label.length > 10) {
    return label.substring(0, 4) + "..." + label.substring(label.length - 4);
  }
  return label;
}

let dataEntries = btcStakingTxHash.values.map((txHash, i) => ({
  btc_staking_tx_hash: txHash,
  covenant_btc_pk: covenantBtcPk.values[i],
  status: status.values[i],
  signed_height: signedHeight.values[i],
  delegation_height: delegationHeight.values[i]
}));

dataEntries.sort((a, b) => b.delegation_height - a.delegation_height);

let uniqueBtcStakingTxHashs = [...new Set(dataEntries.map(entry => entry.btc_staking_tx_hash))];
let uniqueCovenantBtcPks = [...new Set(dataEntries.map(entry => entry.covenant_btc_pk))];

let zValues = Array(uniqueCovenantBtcPks.length).fill(null).map(() => Array(uniqueBtcStakingTxHashs.length).fill(0));
let hoverText = Array(uniqueCovenantBtcPks.length).fill(null).map(() => Array(uniqueBtcStakingTxHashs.length).fill(""));

for (let entry of dataEntries) {
  let rowIndex = uniqueCovenantBtcPks.indexOf(entry.covenant_btc_pk);
  let colIndex = uniqueBtcStakingTxHashs.indexOf(entry.btc_staking_tx_hash);

  if (rowIndex !== -1 && colIndex !== -1 && entry.status !== null) {
    let statusValue = entry.status ? 1 : 0;
    zValues[rowIndex][colIndex] = statusValue;

    hoverText[rowIndex][colIndex] = `
    BTC_Staking_Tx_Hash: <b>${entry.btc_staking_tx_hash}</b><br>
    Committee_BTC_PK: <b>${entry.covenant_btc_pk}</b><br>
    Delegation_Height: <b>${entry.delegation_height}</b><br>
    Signed_Height: <b>${entry.signed_height}</b><br>
    Status: <b>${entry.status ? "‚úÖ Signed" : "‚ùå Missed"}</b>
    `;
  }
}

let colorScale = [
  [0, 'rgba(255, 0, 0, 0.5)'], // Missed = red
  [1, 'rgba(0, 255, 0, 0.5)'], // Success = green
];

return {
  data: [{
    z: zValues,
    x: uniqueBtcStakingTxHashs,
    y: uniqueCovenantBtcPks,
    type: 'heatmap',
    colorscale: colorScale,
    showscale: false,
    zmin: 0,
    zmax: 1,
    xgap: 5,
    ygap: 5,
    text: hoverText,
    hoverinfo: 'text'
  }],
  layout: {
    // title: "BTC Staking Heatmap",
    xaxis: {
      title: "BTC Staking TX Hash",
      type: 'category',
      tickmode: 'array',
      tickvals: uniqueBtcStakingTxHashs,
      ticktext: uniqueBtcStakingTxHashs.map(trimLabel),
      tickangle: -45,
      showgrid: true
    },
    yaxis: {
      title: "Covenant BTC PK",
      tickmode: 'array',
      tickvals: uniqueCovenantBtcPks,
      ticktext: uniqueCovenantBtcPks.map(trimLabel),
      showgrid: true
    },
    margin: { l: 200, t: 40, b: 100 }
  }
};

