// Use epoch as the X-axis
let uniqueEpochs = [...new Set(epoch.values)].sort((a, b) => b - a);  // Sort descending
let uniqueMonikers = [...new Set(moniker.values)].sort().reverse();

// Initialize z-values and hover text
let zValues = Array(uniqueMonikers.length).fill(null).map(() => Array(uniqueEpochs.length).fill(0));
